mapper.select:
select
distinctrow
m.module_id,
m.parent_module_id,
m.module_name,
m.module_code_seq,
m.module_value,
m.module_type,
m.module_group
from sys_module m
join sys_role_module rm on m.module_id = rm.module_id
and rm.role_id = #{roleId}
and rm.tenant_id = #{tenantId}
and m.module_type in #{moduleType}
where rm.role_id = #{roleId}
and rm.tenant_id = #{tenantId}
and m.module_type in #{moduleType}

select distinct r.role_id, r.role_name, r.remark, r.enabled, r.create_time from sys_role r join sys_employee_role er on r.role_id = er.role_id and er.employee_id = #{employeeId} and r.tenant_id = #{tenantId}
and r.build_in = false
and er.tenant_id = #{tenantId} and r.enabled = true and r.delete_time is null where er.employee_id = #{employeeId} and r.tenant_id = #{tenantId}
and r.build_in = false
and er.tenant_id = #{tenantId} and r.enabled = true and r.delete_time is null
